package offgame;

public class SwapNumberExampleWithOutThirdDigit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// a= 10 b = 20;
		
	
		int a =75;
		int b =20;
		
		System.out.println(a +" " + b);
		
		b=a+b;//30
		System.out.println(" ///////////////" + b); //75+20//95
		a=b-a; // 
		System.out.println(" ///////////////" + a); //  95-75= 20
		b=b-a;
		System.out.println(" ///////////////" + b);  
		
		 System.out.println(a +" " + b);

	}

}
